} impl Point { pub fn raw(&self) -> *const RawPoint { &self.raw } // 如果听信 lint 的建议，将上面的 raw 函数参数 self 的引用去掉就是 raw_linted 函数 pub fn raw_linted(self) -> *const RawPoint { &self.raw }
