} 【正例】 use std::fmt; // 为泛型类型 T 指派 Debug triat 限定
fn some_func<T: fmt::Debug>(foo: T) { println!("{:?}", foo); } struct A; fn main() { some_func(5i32); // A 没有实现 Debug trait，会被排除掉 some_func(A); // error[E0277]: `A` doesn't implement `Debug`
}

