let a: f64 = 123456.0;
let b: i64 = unsafe {
std::mem::transmute(a)
};
println!("{}, {}", a, b); // 123456, 4683220244930494464 【正例】 let a: f32 = 123456.0; let b: f64 = a.try_into().expect("trans failed"); println!("{}, {}", a, b); // 123456, 123456

