where [(); W * H]: Sized,
{ array: [T; W * H],
} impl<T, const W: usize, const H: usize> Default for Grid<T, W, H>
where [(); W * H]: Sized, T: Default + Copy,
{ fn default() -> Self { Self { array: [T::default(); W * H], } }
} const WIDTH: usize = 300;
const HEIGHT: usize = 200; fn main(){ let _g = Grid::<usize, 3, 4>::default(); let _h = Grid::<usize, WIDTH, HEIGHT>::default();
}

